import model.TargetConfiguration
import model.TargetGroup
import org.apache.maven.execution.MavenSession
import org.apache.maven.project.MavenProject

/**
 * Service responsible for organizing targets into logical groups based on Maven phases.
 */
class TargetGroupService(
    private val executionPlanAnalysisService: ExecutionPlanAnalysisService
) {

    /**
     * Generate target groups for a project based on its targets
     * @param project The Maven project to generate groups for
     * @param projectTargets The targets to organize into groups
     * @param session The Maven session to extract execution plan phases
     * @return Map of phase name to target group
     * @throws IllegalArgumentException if project or projectTargets is null
     */
    fun generateTargetGroups(
        project: MavenProject,
        projectTargets: Map<String, TargetConfiguration>,
        session: MavenSession
    ): Map<String, TargetGroup> {
        val targetGroups = linkedMapOf<String, TargetGroup>()

        // Get all phases from all 3 Maven lifecycles (default, clean, site)
        val phases = getAllLifecyclePhases()
        val phaseDescriptions = getPhaseDescriptions()

        // Create target groups for each phase
        phases.forEachIndexed { index, phase ->
            val group = TargetGroup(phase, phaseDescriptions[phase], index)

            // Add targets that belong to this phase
            projectTargets.values
                .filter { target -> target.metadata?.phase == phase }
                .forEach { target ->
                    val targetName = projectTargets.entries
                        .find { it.value == target }?.key
                    targetName?.let { group.addTarget(it) }
                }

            // Only include groups that have targets
            if (group.targets.isNotEmpty()) {
                targetGroups[phase] = group
            }
        }

        return targetGroups
    }

    /**
     * Get all lifecycle phases in order
     */
    private fun getAllLifecyclePhases(): List<String> {
        val allPhases = mutableListOf<String>()

        // Add phases in lifecycle order
        allPhases.addAll(executionPlanAnalysisService.getDefaultLifecyclePhases())
        allPhases.addAll(executionPlanAnalysisService.getCleanLifecyclePhases())
        allPhases.addAll(executionPlanAnalysisService.getSiteLifecyclePhases())

        return allPhases.distinct()
    }

    /**
     * Get descriptions for Maven phases
     */
    private fun getPhaseDescriptions(): Map<String, String> {
        return mapOf(
            // Default lifecycle
            "validate" to "Validate the project is correct and all necessary information is available",
            "initialize" to "Initialize build state, e.g. set properties or create directories",
            "generate-sources" to "Generate any source code for inclusion in compilation",
            "process-sources" to "Process the source code, for example to filter any values",
            "generate-resources" to "Generate resources for inclusion in the package",
            "process-resources" to "Copy and process the resources into the destination directory",
            "compile" to "Compile the source code of the project",
            "process-classes" to "Post-process the generated files from compilation",
            "generate-test-sources" to "Generate any test source code for inclusion in compilation",
            "process-test-sources" to "Process the test source code",
            "generate-test-resources" to "Create resources for testing",
            "process-test-resources" to "Copy and process the test resources",
            "test-compile" to "Compile the test source code",
            "process-test-classes" to "Post-process the generated test files",
            "test" to "Run tests using a suitable unit testing framework",
            "prepare-package" to "Perform any operations necessary to prepare a package",
            "package" to "Take the compiled code and package it in its distributable format",
            "pre-integration-test" to "Perform actions required before integration tests",
            "integration-test" to "Process and deploy the package if necessary into an environment",
            "post-integration-test" to "Perform actions required after integration tests",
            "verify" to "Run any checks to verify the package is valid",
            "install" to "Install the package into the local repository",
            "deploy" to "Copy the final package to the remote repository",

            // Clean lifecycle
            "pre-clean" to "Execute processes needed prior to the actual project cleaning",
            "clean" to "Remove all files generated by the prior build",
            "post-clean" to "Execute processes needed to finalize the project cleaning",

            // Site lifecycle
            "pre-site" to "Execute processes needed prior to the actual project site generation",
            "site" to "Create the project's site documentation",
            "post-site" to "Execute processes needed to finalize the site generation",
            "site-deploy" to "Deploy the generated site documentation to the specified web server"
        )
    }
}

