 20    /**
 21     * Generate CreateNodesV2 results from Maven project list
 22     * Returns: List<CreateNodesV2Entry>
 23     */
 24    public static List<CreateNodesV2Entry> generateCreateNodesV2Results(List<MavenProject> projects, File workspaceRoot, Map<MavenProject, Map<String, TargetConfiguration>> projectTargets, Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups) {
 25        List<CreateNodesV2Entry> results = new ArrayList<>();
 26        
 27        for (MavenProject project : projects) {
 28            File pomFile = new File(project.getBasedir(), "pom.xml");
 29            
 30            // Create tuple: [pomFilePath, CreateNodesResult]
 31            String pomPath = NxPathUtils.getRelativePath(workspaceRoot, pomFile);
 32            if (pomPath.isEmpty()) {
 33                pomPath = "pom.xml";
 34            }
 35            
 36            CreateNodesResult createNodesResult = generateCreateNodesResult(project, workspaceRoot, projectTargets.get(project), projectTargetGroups.get(project));
 37            CreateNodesV2Entry entry = new CreateNodesV2Entry(pomPath, createNodesResult);
 38            
 39            results.add(entry);
 40        }
 41        
 42        return results;
 43    }
 44    
 45    /**
 46     * Generate a single CreateNodesResult for a project
 47     */
 48    private static CreateNodesResult generateCreateNodesResult(MavenProject project, File workspaceRoot, Map<String, TargetConfiguration> targets, Map<String, TargetGroup> targetGroups) {
 49        String projectName = NxPathUtils.getProjectName(project);
 50        String projectRoot = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
 51        
 52        // Handle root project case
 53        if (projectRoot.isEmpty()) {
 54            projectRoot = ".";
 55        }
 56        
 57        // Create ProjectConfiguration
 58        ProjectConfiguration projectConfig = new ProjectConfiguration();
 59        projectConfig.setName(project.getGroupId() + ":" + project.getArtifactId());
 60        projectConfig.setRoot(projectRoot);
 61        
 62        
 63        // Add targets (update paths to use workspace-relative paths)
 64        if (targets != null && !targets.isEmpty()) {
 65            Map<String, TargetConfiguration> updatedTargets = updateTargetPaths(targets, workspaceRoot, project.getBasedir());
 66            projectConfig.setTargets(updatedTargets);
 67        }
 68        
 69        // Add metadata
 70        ProjectMetadata metadata = new ProjectMetadata(
 71            project.getGroupId(),
 72            project.getArtifactId(), 
 73            project.getVersion(),
 74            project.getPackaging()
 75        );
 76        
 77        // Add target groups to metadata (convert from Map<String, TargetGroup> to Map<String, List<String>>)
 78        if (targetGroups != null && !targetGroups.isEmpty()) {
