 29    public static List<RawProjectGraphDependency> generateCreateDependencies(List<MavenProject> projects, File workspaceRoot, Log log, boolean verbose) {
 30        List<RawProjectGraphDependency> dependencies = new ArrayList<>();
 31
 32        // Extract workspace projects
 33        Map<String, String> artifactToProject = buildArtifactMapping(projects);
 34
 35        if (verbose && log != null) {
 36            log.info("Built artifact mapping for " + artifactToProject.size() + " workspace projects");
 37        }
