 50            }
 51
 52            // 1. Static dependencies - direct Maven dependencies
 53            int prevStatic = staticDeps;
 54            staticDeps += addStaticDependencies(dependencies, project, artifactToProject, sourceFile);
 55
 56            // 2. Skip implicit dependencies for better performance
 57            // They're rarely needed and cause O(n) complexity
 58            // int prevImplicit = implicitDeps;
