 83                String key = MavenUtils.formatProjectKey(project);
 84                String projectName = MavenUtils.formatProjectKey(project);
 85                mapping.put(key, projectName);
 86            } else {
 87                System.err.println("Warning: Skipping project with null groupId or artifactId: " + project);
 88            }
 89        }
 90        return mapping;
 91    }
