113                        RawProjectGraphDependency dependency = new RawProjectGraphDependency(
114                            source, target, RawProjectGraphDependency.DependencyType.STATIC, sourceFile);
115                        dependencies.add(dependency);
116                        count++;
117                    }
118                }
119            }
120        }
121
122        return count;
123    }
