  1import {
  2  CreateDependencies,
  3  CreateNodesV2,
  4  CreateNodesContextV2,
  5  CreateNodesResultV2,
  6  workspaceRoot,
  7  readJsonFile,
  8  writeJsonFile,
  9} from '@nx/devkit';
 10import { dirname, join } from 'path';
 11import { existsSync, readFileSync } from 'fs';
 12import { spawn } from 'child_process';
 13import { workspaceDataDirectory } from 'nx/src/utils/cache-directory';
 14import { hashObject } from 'nx/src/devkit-internals';
 15import { calculateHashForCreateNodes } from '@nx/devkit/src/utils/calculate-hash-for-create-nodes';
 16
 17export interface MavenPluginOptions {
 18  mavenExecutable?: string;
 19  verbose?: boolean;
 20}
 21
 22const DEFAULT_OPTIONS: MavenPluginOptions = {
 23  mavenExecutable: 'mvn',
 24};
 25
 26// Cache management functions
 27function readMavenCache(cachePath: string): Record<string, any> {
 28  try {
 29    return existsSync(cachePath) ? readJsonFile(cachePath) : {};
 30  } catch {
 31    return {};
 32  }
 33}
 34
 35function writeMavenCache(cachePath: string, results: any) {
 36  try {
 37    writeJsonFile(cachePath, results);
 38  } catch (error) {
 39    console.warn('Failed to write Maven cache:', error.message);
 40  }
 41}
 42
 43/**
 44 * Maven plugin that delegates to Java for analysis and returns results directly
 45 */
 46export const createNodesV2: CreateNodesV2 = [
 47  '**/pom.xml',
 48  async (configFiles, options, context): Promise<CreateNodesResultV2> => {
 49    const opts: MavenPluginOptions = { ...DEFAULT_OPTIONS, ...(options as MavenPluginOptions) };
 50
 51    if (opts.verbose) {
 52      console.log(`Maven plugin found ${configFiles.length} pom.xml files`);
 53    }
 54
 55    // Filter out unwanted pom.xml files
 56    const filteredFiles = configFiles.filter(file =>
 57      !file.includes('maven-script/') &&
 58      !file.includes('target/') &&
 59      !file.includes('node_modules/')
 60    );
 61
 62    if (filteredFiles.length === 0) {
 63      return [];
 64    }
 65
 66    try {
 67      // Generate cache key based on pom.xml files and options
 68      const projectHash = await calculateHashForCreateNodes(
 69        workspaceRoot,
 70        (options as object) ?? {},
 71        context,
 72        ['{projectRoot}/pom.xml', '{workspaceRoot}/**/pom.xml']
 73      );
 74      const optionsHash = hashObject(opts);
 75      const cacheKey = `nodes-${projectHash}-${optionsHash}`;
 76      
 77      // Set up cache path
 78      const cachePath = join(workspaceDataDirectory, 'maven-analysis-cache.json');
 79      const cache = readMavenCache(cachePath);
 80      
 81      // Check if we have valid cached results
 82      if (cache[cacheKey]) {
 83        if (opts.verbose) {
 84          console.log('Using cached Maven analysis results for createNodes');
 85        }
 86        return cache[cacheKey];
 87      }
 88
 89      // Run analysis if not cached
 90      const result = await runMavenAnalysis(opts);
 91      const createNodesResults = result.createNodesResults || [];
 92
 93      // Cache the results
 94      cache[cacheKey] = createNodesResults;
 95      writeMavenCache(cachePath, cache);
 96
 97      return createNodesResults;
 98
 99    } catch (error) {
100      console.error(`Maven analysis failed:`, error.message);
101      return [];
102    }
103  },
104];
105
106/**
107 * Create dependencies using Java analysis results
108 */
109export const createDependencies: CreateDependencies = async (options, context) => {
110  const opts: MavenPluginOptions = { ...DEFAULT_OPTIONS, ...(options as MavenPluginOptions) };
111
112  try {
113    // Generate cache key based on pom.xml files and options
114    const projectHash = await calculateHashForCreateNodes(
115      workspaceRoot,
116      (options as object) ?? {},
117      context,
118      ['{projectRoot}/pom.xml', '{workspaceRoot}/**/pom.xml']
119    );
120    const optionsHash = hashObject(opts);
121    const cacheKey = `deps-${projectHash}-${optionsHash}`;
122    
123    // Set up cache path
124    const cachePath = join(workspaceDataDirectory, 'maven-analysis-cache.json');
125    const cache = readMavenCache(cachePath);
126    
127    // Check if we have valid cached results
128    if (cache[cacheKey]) {
129      if (opts.verbose) {
130        console.log('Using cached Maven analysis results for createDependencies');
131      }
132      return cache[cacheKey];
133    }
134
135    // Run analysis if not cached
136    const result = await runMavenAnalysis(opts);
137    const createDependencies = result.createDependencies || [];
138
139    // Cache the results
140    cache[cacheKey] = createDependencies;
141    writeMavenCache(cachePath, cache);
142
143    return createDependencies;
144
145  } catch (error) {
146    console.error(`Maven dependency analysis failed:`, error.message);
147    return [];
148  }
149};
150
151/**
152 * Run Maven analysis using Java plugin
153 */
154async function runMavenAnalysis(options: MavenPluginOptions): Promise<any> {
155  const outputFile = join(workspaceDataDirectory, 'maven-analysis.json');
156
157  // Check if verbose mode is enabled
158  const isVerbose = options.verbose || process.env.NX_VERBOSE_LOGGING === 'true' || process.argv.includes('--verbose');
159
160  // Skip Java analyzer check during testing to avoid git HEAD issues
161  if (process.env.NODE_ENV !== 'test') {
162    // Check if Java analyzer is available
163    if (!findJavaAnalyzer()) {
164      throw new Error('Maven analyzer not found. Please ensure maven-plugin is compiled.');
165    }
166  }
167
168  if (isVerbose) {
169    console.log(`Running Maven analysis with verbose logging enabled...`);
170    console.log(`Maven executable: ${options.mavenExecutable}`);
171    console.log(`Output file: ${outputFile}`);
172  }
173
174  // Build Maven command arguments
175  // Use the custom nx:analyze goal from our Java Maven plugin
176  const mavenArgs = [
177    'io.quarkus:maven-plugin:999-SNAPSHOT:analyze',
178    `-Dnx.outputFile=${outputFile}`,
179    `-Dnx.verbose=${isVerbose}`
180  ];
181
182  // Always use quiet mode to suppress expected reactor dependency warnings
183  // These warnings are normal in large multi-module projects and don't affect functionality
184  mavenArgs.push('-q');
185
186  if (isVerbose) {
187    console.log(`Executing Maven command: ${options.mavenExecutable} ${mavenArgs.join(' ')}`);
188    console.log(`Working directory: ${workspaceRoot}`);
189  }
190
191  // Run Maven plugin
192  await new Promise<void>((resolve, reject) => {
193    const child = spawn(options.mavenExecutable, mavenArgs, {
194      cwd: workspaceRoot,
195      stdio: 'inherit'
196    });
197
198    // No timeout for very large codebases like Quarkus
199    child.on('close', (code) => {
200      if (isVerbose) {
201        console.log(`Maven process completed with exit code: ${code}`);
202      }
203      if (code === 0) {
204        if (isVerbose) {
205          console.log(`Maven analysis completed successfully`);
206        }
207        resolve();
208      } else {
209        reject(new Error(`Maven process exited with code ${code}`));
210      }
211    });
212
213    child.on('error', (error) => {
214      reject(new Error(`Failed to spawn Maven process: ${error.message}`));
215    });
216  });
217
218  // Read and return JSON result
219  if (!existsSync(outputFile)) {
220    throw new Error(`Output file not found: ${outputFile}`);
221  }
222
223  const jsonContent = readFileSync(outputFile, 'utf8');
224  return JSON.parse(jsonContent);
225}
226
227/**
228 * Find the compiled Java Maven analyzer
229 */
230function findJavaAnalyzer(): string | null {
231  const possiblePaths = [
232    join(__dirname, 'maven-plugin/target/classes'),
233    join(__dirname, 'maven-plugin/target/maven-plugin-999-SNAPSHOT.jar'),
234  ];
235
236  console.log(possiblePaths);
237
238  for (const path of possiblePaths) {
239    if (existsSync(path)) {
240      return path;
241    }
242  }
243
244  return null;
245}
246
247
248/**
249 * Plugin configuration
250 */
251export default {
252  name: 'maven-plugin',
253  createNodesV2,
254  createDependencies,
255};

