109    /**
110     * Generate targets for plugin goals
111     */
112    public Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot,
113                                                                      Map<String, List<String>> goalDependencies) {
114        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
115
116        if (verbose) {
117            log.debug("Generating plugin goal targets for project: " + project.getArtifactId());
118        }
119
120        final String projectRootToken = "{projectRoot}";
121        final String actualProjectPath;
122
123        if (workspaceRoot != null) {
124            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
125            actualProjectPath = relativePath.isEmpty() ? "." : relativePath;
126        } else {
127            actualProjectPath = "{projectRoot}";
128        }
129
130        if (project.getBuildPlugins() != null) {
131            project.getBuildPlugins().forEach(plugin -> {
132                // Process actual executions from effective POM
133                if (plugin.getExecutions() != null) {
134                    plugin.getExecutions().forEach(execution -> {
135                        if (execution.getGoals() != null) {
