package v2;

import org.apache.maven.execution.MavenSession;
import org.apache.maven.plugin.AbstractMojo;
import org.apache.maven.plugin.MojoExecutionException;
import org.apache.maven.plugin.MojoFailureException;
import org.apache.maven.plugins.annotations.Mojo;
import org.apache.maven.plugins.annotations.Parameter;
import org.apache.maven.plugins.annotations.ResolutionScope;
import org.apache.maven.project.MavenProject;

import com.google.gson.Gson;
import com.google.gson.GsonBuilder;
import model.*;

import java.io.File;
import java.io.IOException;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.List;
import java.util.ArrayList;
import java.util.LinkedHashMap;
import java.util.Map;

@Mojo(name = "nx-analyzer-v2", requiresDependencyResolution = ResolutionScope.COMPILE_PLUS_RUNTIME)
public class NxAnalyzerMojoV2 extends AbstractMojo {

    @Parameter(defaultValue = "${session}", readonly = true, required = true)
    private MavenSession session;

    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
    private List<MavenProject> reactorProjects;

    @Parameter(property = "nx.outputFile", required = true)
    private String outputFile;

    @Parameter(property = "nx.verbose", defaultValue = "false")
    private boolean verbose;

    private final Gson gson = new GsonBuilder().setPrettyPrinting().create();

    @Override
    public void execute() throws MojoExecutionException, MojoFailureException {
        if (verbose) {
            getLog().info("Nx Analyzer V2 - Starting analysis with " + reactorProjects.size() + " projects");
        }

        try {
            // Create analysis result
            AnalysisResult result = analyzeProjects();

            // Write to output file
            writeResults(result);

            if (verbose) {
                getLog().info("Nx Analyzer V2 - Analysis completed successfully");
            }

        } catch (Exception e) {
            throw new MojoExecutionException("Analysis failed", e);
        }
    }

    private AnalysisResult analyzeProjects() {
        AnalysisResult result = new AnalysisResult();

        // Get workspace root
        Path workspaceRoot = Paths.get(session.getExecutionRootDirectory());

        // Create projects
        List<CreateNodesV2Entry> createNodesResults = new ArrayList<>();
        for (MavenProject project : reactorProjects) {
            CreateNodesV2Entry entry = createProjectEntry(project, workspaceRoot);
            if (entry != null) {
                createNodesResults.add(entry);
            }
        }
        result.setCreateNodesResults(createNodesResults);

        // Create dependencies
        List<RawProjectGraphDependency> dependencies = createProjectDependencies();
        result.setCreateDependencies(dependencies);

        return result;
    }

    private CreateNodesV2Entry createProjectEntry(MavenProject project, Path workspaceRoot) {
        try {
            // Calculate relative path from workspace root
            Path projectPath = project.getBasedir().toPath();
            Path relativePath = workspaceRoot.relativize(projectPath);
            String relativePathStr = relativePath.toString().replace('\\', '/');

            // Create project configuration
            ProjectConfiguration config = new ProjectConfiguration();
            config.setName(getProjectName(project));
            config.setRoot(relativePathStr);
            config.setProjectType(determineProjectType(project));

            // Set source root if it exists
            if (project.getCompileSourceRoots() != null && !project.getCompileSourceRoots().isEmpty()) {
                String sourceRoot = project.getCompileSourceRoots().get(0);
                Path sourceRootPath = Paths.get(sourceRoot);
                Path relativeSourceRoot = workspaceRoot.relativize(sourceRootPath);
                config.setSourceRoot(relativeSourceRoot.toString().replace('\\', '/'));
            }

            // Create entry
            CreateNodesV2Entry entry = new CreateNodesV2Entry();
            entry.setPomFilePath(relativePath.resolve("pom.xml").toString().replace('\\', '/'));

            CreateNodesResult result = new CreateNodesResult();
            result.addProject(config.getName(), config);
            entry.setNodes(result);

            if (verbose) {
                getLog().info("Created project: " + config.getName() + " at " + config.getRoot());
            }

            return entry;

        } catch (Exception e) {
            getLog().warn("Failed to create project entry for " + project.getName() + ": " + e.getMessage());
            return null;
        }
    }

    private String getProjectName(MavenProject project) {
        // Use artifactId as project name, handling multi-module scenarios
        String artifactId = project.getArtifactId();

        // If this is a child module, include parent context if needed
        if (project.getParent() != null && !project.getParent().getArtifactId().equals("maven")) {
            String parentArtifactId = project.getParent().getArtifactId();
            // Only prefix if the artifactId doesn't already contain the parent name
            if (!artifactId.startsWith(parentArtifactId)) {
                return parentArtifactId + "-" + artifactId;
            }
        }

        return artifactId;
    }

    private String determineProjectType(MavenProject project) {
        String packaging = project.getPackaging();

        switch (packaging) {
            case "jar":
                return "library";
            case "war":
            case "ear":
                return "application";
            case "pom":
                return "library"; // Parent/aggregator projects
            default:
                return "library";
        }
    }

    private List<RawProjectGraphDependency> createProjectDependencies() {
        List<RawProjectGraphDependency> dependencies = new ArrayList<>();

        // Create dependencies between projects based on Maven dependencies
        for (MavenProject project : reactorProjects) {
            String sourceProject = getProjectName(project);

            // Check dependencies
            if (project.getDependencies() != null) {
                project.getDependencies().forEach(dep -> {
                    // Only create dependencies for projects within this reactor
                    String depArtifactId = dep.getArtifactId();
                    MavenProject targetProject = findProjectByArtifactId(depArtifactId);

                    if (targetProject != null) {
                        String targetProjectName = getProjectName(targetProject);
                        if (!sourceProject.equals(targetProjectName)) {
                            RawProjectGraphDependency dependency = new RawProjectGraphDependency(
                                sourceProject,
                                targetProjectName,
                                RawProjectGraphDependency.DependencyType.STATIC,
                                project.getFile().getName()
                            );
                            dependencies.add(dependency);

                            if (verbose) {
                                getLog().info("Created dependency: " + sourceProject + " -> " + targetProjectName);
                            }
                        }
                    }
                });
            }
        }

        return dependencies;
    }

    private MavenProject findProjectByArtifactId(String artifactId) {
        return reactorProjects.stream()
            .filter(p -> p.getArtifactId().equals(artifactId))
            .findFirst()
            .orElse(null);
    }

    private void writeResults(AnalysisResult result) throws IOException {
        File output = new File(outputFile);
        output.getParentFile().mkdirs();

        try (java.io.FileWriter writer = new java.io.FileWriter(output)) {
            gson.toJson(result, writer);
        }

        if (verbose) {
            getLog().info("Results written to: " + output.getAbsolutePath());
        }
    }

    /**
     * Simple wrapper for analysis results
     */
    public static class AnalysisResult {
        private List<CreateNodesV2Entry> createNodesResults = new ArrayList<>();
        private List<RawProjectGraphDependency> createDependencies = new ArrayList<>();

        public List<CreateNodesV2Entry> getCreateNodesResults() { return createNodesResults; }
        public void setCreateNodesResults(List<CreateNodesV2Entry> createNodesResults) { this.createNodesResults = createNodesResults; }

        public List<RawProjectGraphDependency> getCreateDependencies() { return createDependencies; }
        public void setCreateDependencies(List<RawProjectGraphDependency> createDependencies) { this.createDependencies = createDependencies; }
    }
}

