  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.DefaultLifecycles;
  4import org.apache.maven.plugin.AbstractMojo;
  5import org.apache.maven.plugin.MojoExecutionException;
  6import org.apache.maven.plugin.MojoFailureException;
  7import org.apache.maven.plugins.annotations.Component;
  8import org.apache.maven.plugins.annotations.Mojo;
  9import org.apache.maven.plugins.annotations.Parameter;
 10import org.apache.maven.plugins.annotations.ResolutionScope;
 11import org.apache.maven.project.MavenProject;
 12
 13import com.google.gson.Gson;
 14import com.google.gson.GsonBuilder;
 15
 16import model.CreateNodesV2Entry;
 17import model.RawProjectGraphDependency;
 18import model.TargetConfiguration;
 19import model.TargetGroup;
 20import java.io.File;
 21import java.io.FileWriter;
 22import java.io.IOException;
 23import java.util.ArrayList;
 24import java.util.HashMap;
 25import java.util.LinkedHashMap;
 26import java.util.LinkedHashSet;
 27import java.util.List;
 28import java.util.Map;
 29import java.util.Set;
 30
 31/**
 32 * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 33 * This version delegates complex logic to specialized service classes.
 34 */
 35@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 36public class NxAnalyzerMojo extends AbstractMojo {
 37    
 38    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 39    private MavenSession session;
 40    
 41    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 42    private List<MavenProject> reactorProjects;
 43    
 44    @Parameter(property = "nx.outputFile")
 45    private String outputFile;
 46    
 47    @Parameter(property = "nx.verbose", defaultValue = "false")
 48    private String verboseStr;
 49    
 50    @Component
 51    private LifecycleExecutor lifecycleExecutor;
 52    
 53    @Component
 54    private DefaultLifecycles defaultLifecycles;
 55    
 56    // Services for delegating complex operations
 57    private ExecutionPlanAnalysisService executionPlanAnalysisService;
 58    private TargetGenerationService targetGenerationService;
 59    private TargetGroupService targetGroupService;
 60    private TargetDependencyService targetDependencyService;
 61    
 62    @Override
 63    public void execute() throws MojoExecutionException, MojoFailureException {
 64        long overallStartTime = System.currentTimeMillis();
 65        
 66        getLog().info(" Starting Maven analysis for " + reactorProjects.size() + " projects...");
 67        
 68        try {
 69            // Phase 1: Initialize services
 70            long initStart = System.currentTimeMillis();
 71            initializeServices();
 72            long initDuration = System.currentTimeMillis() - initStart;
 73            getLog().info("  Service initialization completed in " + initDuration + "ms");
 74            
 75            logBasicInfo();
 76            
 77            // Phase 2: Perform Maven analysis
 78            long analysisStart = System.currentTimeMillis();
 79            getLog().info(" Starting Maven workspace analysis...");
 80            Map<String, Object> result = performAnalysis();
 81            long analysisDuration = System.currentTimeMillis() - analysisStart;
 82            getLog().info("  Maven workspace analysis completed in " + analysisDuration + "ms (" + 
 83                         String.format("%.2f", analysisDuration / 1000.0) + "s)");
 84            
 85            // Phase 3: Write output
 86            long writeStart = System.currentTimeMillis();
 87            String outputPath = determineOutputPath();
 88            writeResult(result, outputPath);
 89            long writeDuration = System.currentTimeMillis() - writeStart;
 90            getLog().info("  Output file writing completed in " + writeDuration + "ms");
 91            
 92            logCompletion(overallStartTime, outputPath, analysisDuration);
 93            
 94        } catch (Exception e) {
 95            long failureDuration = System.currentTimeMillis() - overallStartTime;
 96            getLog().error(" Analysis failed after " + failureDuration + "ms: " + e.getMessage());
 97            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 98        }
 99    }
100    
101    private void initializeServices() {
102        this.executionPlanAnalysisService = new ExecutionPlanAnalysisService(getLog(), isVerbose(), lifecycleExecutor, session, defaultLifecycles);
103        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose(), session, executionPlanAnalysisService);
104        this.targetGroupService = new TargetGroupService(executionPlanAnalysisService);
105        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), executionPlanAnalysisService);
106    }
107    
108    private void logBasicInfo() {
109        if (isVerbose()) {
110            getLog().info("Verbose mode enabled");
111        }
112        
113        getLog().info("Root directory: " + session.getExecutionRootDirectory());
114        if (!reactorProjects.isEmpty()) {
115            MavenProject first = reactorProjects.get(0);
116            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
117            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
118            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
119        }
120    }
121    
122    private Map<String, Object> performAnalysis() {
123        File workspaceRoot = new File(session.getExecutionRootDirectory());
124        
125        // Phase 1: Calculate project dependencies
126        long depCalcStart = System.currentTimeMillis();
127        Map<MavenProject, List<MavenProject>> projectDependencies = calculateProjectDependencies();
128        long depCalcDuration = System.currentTimeMillis() - depCalcStart;
129        getLog().info("  Project dependency calculation completed in " + depCalcDuration + "ms");
130        
131        // Phase 2: Generate targets and groups for each project
132        long targetGenStart = System.currentTimeMillis();
133        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
134        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
135        
136        int processedProjects = 0;
137        for (MavenProject project : reactorProjects) {
138            long projectStart = System.currentTimeMillis();
139            try {
140                // Get actual project dependencies for this project (not all reactor projects)
141                List<MavenProject> actualDependencies = projectDependencies.getOrDefault(project, new ArrayList<>());
142                
143                // Calculate goal dependencies using only actual dependencies
144                Map<String, List<String>> goalDependencies = calculateGoalDependencies(project, actualDependencies);
145                
146                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
147                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
148                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
149                
150                // Now calculate phase dependencies using the generated targets
151                Map<String, List<String>> phaseDependencies = calculatePhaseDependencies(project, targets);
152                
153                // Update phase targets with calculated dependencies
154                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
155                projectTargets.put(project, targets);
156                
157                // Generate target groups
158                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
159                projectTargetGroups.put(project, targetGroups);
160                
161                processedProjects++;
162                long projectDuration = System.currentTimeMillis() - projectStart;
163                
164                if (isVerbose()) {
165                    getLog().info(" Processed " + project.getArtifactId() + " in " + projectDuration + "ms" +
166                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups, " +
167                                 actualDependencies.size() + " project dependencies");
168                } else if (processedProjects % 100 == 0) {
169                    // Log progress every 100 projects for large workspaces
170                    long avgTimePerProject = (System.currentTimeMillis() - targetGenStart) / processedProjects;
171                    getLog().info(" Processed " + processedProjects + "/" + reactorProjects.size() + 
172                                 " projects (avg " + avgTimePerProject + "ms per project)");
173                }
174                
175            } catch (Exception e) {
176                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
177                // Continue with empty targets and groups
178                projectTargets.put(project, new LinkedHashMap<>());
179                projectTargetGroups.put(project, new LinkedHashMap<>());
180            }
181        }
182        
183        long targetGenDuration = System.currentTimeMillis() - targetGenStart;
184        getLog().info("  Target generation completed in " + targetGenDuration + "ms for " + 
185                     processedProjects + " projects (avg " + (targetGenDuration / processedProjects) + "ms per project)");
186        
187        // Phase 3: Generate Nx-compatible outputs
188        long outputGenStart = System.currentTimeMillis();
189        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
190            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
191        
192        List<Object[]> createNodesResults = new ArrayList<>();
193        for (CreateNodesV2Entry entry : createNodesEntries) {
194            createNodesResults.add(entry.toArray());
195        }
196        
197        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
198            reactorProjects, workspaceRoot, getLog(), isVerbose());
199        
200        long outputGenDuration = System.currentTimeMillis() - outputGenStart;
201        getLog().info("  Nx output generation completed in " + outputGenDuration + "ms");
202        
203        if (isVerbose()) {
204            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
205        }
206        
207        Map<String, Object> result = new LinkedHashMap<>();
208        result.put("createNodesResults", createNodesResults);
209        result.put("createDependencies", createDependencies);
210        
211        return result;
212    }
213    
214    /**
215     * Calculate actual project dependencies for all projects in the reactor.
216     * Returns a map from each project to its list of actual Maven dependency projects.
217     */
218    private Map<MavenProject, List<MavenProject>> calculateProjectDependencies() {
219        Map<MavenProject, List<MavenProject>> projectDependencies = new LinkedHashMap<>();
220        
221        // Build artifact mapping for workspace projects
222        Map<String, MavenProject> artifactToProject = new HashMap<>();
223        for (MavenProject project : reactorProjects) {
224            if (project.getGroupId() != null && project.getArtifactId() != null) {
225                String key = MavenUtils.formatProjectKey(project);
226                artifactToProject.put(key, project);
227            }
228        }
229        
230        // Calculate dependencies for each project
231        for (MavenProject project : reactorProjects) {
232            List<MavenProject> dependencies = new ArrayList<>();
233            
234            if (project.getDependencies() != null) {
235                for (org.apache.maven.model.Dependency dep : project.getDependencies()) {
236                    if (dep.getGroupId() != null && dep.getArtifactId() != null) {
237                        String depKey = dep.getGroupId() + ":" + dep.getArtifactId();
238                        
239                        // Check if this dependency refers to another project in workspace
240                        MavenProject targetProject = artifactToProject.get(depKey);
241                        if (targetProject != null && !targetProject.equals(project)) {
242                            dependencies.add(targetProject);
243                        }
244                    }
245                }
246            }
247            
248            projectDependencies.put(project, dependencies);
249            
250            if (isVerbose() && !dependencies.isEmpty()) {
251                getLog().debug("Project " + project.getArtifactId() + " depends on " + 
252                             dependencies.size() + " workspace projects");
253            }
254        }
255        
256        return projectDependencies;
257    }
258
259    /**
260     * Calculate goal dependencies for a project using only actual project dependencies
261     */
262    private Map<String, List<String>> calculateGoalDependencies(MavenProject project, List<MavenProject> actualDependencies) {
263        Map<String, List<String>> goalDependencies = new LinkedHashMap<>();
264        
265        if (isVerbose()) {
266            getLog().debug("Calculating goal dependencies for " + project.getArtifactId());
267        }
268        
269        // First pass: collect all potential goal targets
270        Set<String> goalTargets = collectGoalTargets(project);
271        
272        // Calculate dependencies for each goal target
273        for (String targetName : goalTargets) {
274            String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
275            
276            // Try to find execution phase from plugin configuration
277            String executionPhase = findExecutionPhase(project, targetName);
278            
279            List<String> dependencies = targetDependencyService.calculateGoalDependencies(
280                project, executionPhase, targetName, actualDependencies);
281            goalDependencies.put(targetName, dependencies);
282        }
283        
284        return goalDependencies;
285    }
286    
287    /**
288     * Calculate phase dependencies for a project using generated targets
289     */
290    private Map<String, List<String>> calculatePhaseDependencies(MavenProject project, Map<String, TargetConfiguration> allTargets) {
291        Map<String, List<String>> phaseDependencies = new LinkedHashMap<>();
292        
293        // Get all lifecycle phases from all Maven lifecycles (default, clean, site)
294        Set<String> allPhases = executionPlanAnalysisService.getAllLifecyclePhases();
295        
296        if (isVerbose()) {
297            getLog().debug("Calculating dependencies for " + allPhases.size() + " lifecycle phases: " + allPhases);
298        }
299        
300        for (String phase : allPhases) {
301            List<String> dependencies = targetDependencyService.calculatePhaseDependencies(
302                phase, allTargets, project, reactorProjects);
303            phaseDependencies.put(phase, dependencies);
304        }
305        
306        return phaseDependencies;
307    }
308    
309    /**
310     * Update phase targets with calculated dependencies
311     */
312    private void updatePhaseTargetsWithDependencies(Map<String, TargetConfiguration> targets, 
313                                                   Map<String, List<String>> phaseDependencies) {
314        for (Map.Entry<String, List<String>> entry : phaseDependencies.entrySet()) {
315            String phase = entry.getKey();
316            List<String> dependencies = entry.getValue();
317            
318            TargetConfiguration phaseTarget = targets.get(phase);
319            if (phaseTarget != null) {
320                phaseTarget.setDependsOn(dependencies);
321            }
322        }
323    }
324    
325    /**
326     * Collect all potential goal targets from project plugins
327     */
328    private Set<String> collectGoalTargets(MavenProject project) {
329        Set<String> goalTargets = new LinkedHashSet<>();
330        
331        if (isVerbose()) {
332            getLog().debug("Collecting goals for " + project.getArtifactId() + " (" + 
333                          (project.getBuildPlugins() != null ? project.getBuildPlugins().size() : 0) + " plugins)");
334        }
335        
336        if (project.getBuildPlugins() != null) {
337            project.getBuildPlugins().forEach(plugin -> {
338                String artifactId = plugin.getArtifactId();
339                
340                // Add goals from executions
341                if (plugin.getExecutions() != null) {
342                    plugin.getExecutions().forEach(execution -> {
343                        if (execution.getGoals() != null) {
344                            execution.getGoals().forEach(goal -> {
345                                String targetName = ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
346                                goalTargets.add(targetName);
347                            });
348                        }
349                    });
350                }
351                
352                // Add common goals for well-known plugins
353                addCommonGoals(artifactId, goalTargets);
354            });
355        }
356        
357        if (isVerbose()) {
358            getLog().debug("Found " + goalTargets.size() + " goals for " + project.getArtifactId());
359        }
360        return goalTargets;
361    }
362    
363    /**
364     * Find execution phase for a goal target
365     */
366    private String findExecutionPhase(MavenProject project, String targetName) {
367        String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
368        
369        if (project.getBuildPlugins() != null) {
370            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
371                if (plugin.getExecutions() != null) {
372                    for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
373                        if (execution.getGoals() != null && execution.getGoals().contains(goal)) {
374                            return execution.getPhase();
375                        }
376                    }
377                }
378            }
379        }
380        
381        return null; // Will trigger phase inference in dependency service
382    }
383    
384    /**
385     * Add common goals for well-known plugins
386     */
387    private void addCommonGoals(String artifactId, Set<String> goalTargets) {
388        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
389        for (String goal : commonGoals) {
390            goalTargets.add(ExecutionPlanAnalysisService.getTargetName(artifactId, goal));
391        }
392    }
393    
394    
395    private boolean isVerbose() {
396        String systemProp = System.getProperty("nx.verbose");
397        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
398        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
399        return fromParam || fromSystem;
400    }
401    
402    private String determineOutputPath() {
403        if (outputFile != null && !outputFile.isEmpty()) {
404            return outputFile;
405        }
406        return new File(session.getExecutionRootDirectory(), "maven-analysis.json").getAbsolutePath();
407    }
408    
409    private void writeResult(Map<String, Object> result, String outputPath) throws IOException {
410        Gson gson = new GsonBuilder()
411            .setPrettyPrinting()
412            .serializeNulls()
413            .create();
414            
415        try (FileWriter writer = new FileWriter(outputPath)) {
416            gson.toJson(result, writer);
417        }
418        
419        if (isVerbose()) {
420            long fileSize = new File(outputPath).length();
421            getLog().info("Wrote " + (fileSize / 1024) + "KB analysis results");
422        }
423    }
424    
425    private void logCompletion(long startTime, String outputPath, long analysisDuration) {
426        long totalTime = System.currentTimeMillis() - startTime;
427        
428        // Calculate analysis percentage of total time
429        double analysisPercentage = (analysisDuration * 100.0) / totalTime;
430        
431        // Always show comprehensive timing information
432        getLog().info(" Maven analysis completed successfully!");
433        getLog().info(" Total execution time: " + String.format("%.2f", totalTime / 1000.0) + "s");
434        getLog().info(" Core analysis time: " + String.format("%.2f", analysisDuration / 1000.0) + "s (" + 
435                     String.format("%.1f", analysisPercentage) + "% of total)");
436        getLog().info(" Output written to: " + outputPath);
437        getLog().info("  Projects analyzed: " + reactorProjects.size());
438        
439        // Performance insights
440        if (reactorProjects.size() > 0) {
441            double avgTimePerProject = analysisDuration / (double) reactorProjects.size();
442            getLog().info("  Average time per project: " + String.format("%.1f", avgTimePerProject) + "ms");
443            
444            if (avgTimePerProject > 1000) {
445                getLog().warn("  High average time per project detected - consider optimizing large projects");
446            } else if (avgTimePerProject < 10) {
447                getLog().info(" Excellent performance - very fast analysis per project");
448            }
449        }
450    }
451}

