import org.apache.maven.execution.MavenSession
import org.apache.maven.lifecycle.LifecycleExecutor
import org.apache.maven.lifecycle.MavenExecutionPlan
import org.apache.maven.lifecycle.DefaultLifecycles
import org.apache.maven.plugin.MojoExecution
import org.apache.maven.plugin.logging.Log
import org.apache.maven.project.MavenProject

import java.util.*
import java.util.concurrent.ConcurrentHashMap

/**
 * Service that analyzes Maven execution plans once and caches results for efficient querying.
 * This avoids recalculating execution plans multiple times for the same project.
 */
class ExecutionPlanAnalysisService(
    private val log: Log,
    private val verbose: Boolean,
    private val lifecycleExecutor: LifecycleExecutor,
    private val session: MavenSession,
    private val defaultLifecycles: DefaultLifecycles
) {

    // Cache to store analysis results per project
    private val analysisCache = ConcurrentHashMap<String, ProjectExecutionAnalysis>()

    /**
     * Get or compute execution analysis for a project
     */
    fun getAnalysis(project: MavenProject): ProjectExecutionAnalysis {
        val projectKey = MavenUtils.formatProjectKey(project)
        return analysisCache.computeIfAbsent(projectKey) { analyzeProject(project) }
    }

    /**
     * Find the phase for a specific goal
     */
    fun findPhaseForGoal(project: MavenProject?, goal: String?): String? {
        if (project == null || goal.isNullOrEmpty()) {
            return null
        }
        val analysis = getAnalysis(project)
        return analysis.getPhaseForGoal(goal)
    }

    /**
     * Get all goals for a specific phase
     */
    fun getGoalsForPhase(project: MavenProject?, phase: String?): List<String> {
        if (project == null || phase == null) {
            return arrayListOf()
        }
        val analysis = getAnalysis(project)
        return analysis.getGoalsForPhase(phase)
    }

    /**
     * Get all lifecycle phases from all lifecycles (default, clean, site)
     */
    val allLifecyclePhases: Set<String>
        get() {
            val allPhases = LinkedHashSet<String>()
            allPhases.addAll(getLifecyclePhases("default"))
            allPhases.addAll(getLifecyclePhases("clean"))
            allPhases.addAll(getLifecyclePhases("site"))
            return allPhases
        }

    /**
     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
     */
    val defaultLifecyclePhases: List<String>
        get() = getLifecyclePhases("default")

    /**
     * Get clean lifecycle phases (pre-clean, clean, post-clean)
     */
    val cleanLifecyclePhases: List<String>
        get() = getLifecyclePhases("clean")

    /**
     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
     */
    val siteLifecyclePhases: List<String>
        get() = getLifecyclePhases("site")

    /**
     * Get phases for a specific lifecycle
     */
    private fun getLifecyclePhases(lifecycleName: String): List<String> {
        return when (lifecycleName.lowercase()) {
            "default" -> listOf(
                "validate", "initialize", "generate-sources", "process-sources",
                "generate-resources", "process-resources", "compile", "process-classes",
                "generate-test-sources", "process-test-sources", "generate-test-resources",
                "process-test-resources", "test-compile", "process-test-classes", "test",
                "prepare-package", "package", "pre-integration-test", "integration-test",
                "post-integration-test", "verify", "install", "deploy"
            )
            "clean" -> listOf("pre-clean", "clean", "post-clean")
            "site" -> listOf("pre-site", "site", "post-site", "site-deploy")
            else -> emptyList()
        }
    }

    /**
     * Analyze a single project to build execution plan analysis
     */
    private fun analyzeProject(project: MavenProject): ProjectExecutionAnalysis {
        val analysis = ProjectExecutionAnalysis()

        try {
            // Build execution plan for the "verify" phase to get most goals
            val executionPlan = lifecycleExecutor.calculateExecutionPlan(session, "verify")

            // Process each mojo execution
            for (mojoExecution in executionPlan.mojoExecutions) {
                val phase = mojoExecution.lifecyclePhase
                val goal = mojoExecution.goal

                if (phase != null && goal != null) {
                    analysis.addGoalToPhase(phase, goal)
                    analysis.addPhaseForGoal(goal, phase)
                }
            }

            if (verbose) {
                log.debug("Analyzed ${analysis.getTotalGoalCount()} goals across ${analysis.getPhaseCount()} phases for ${project.artifactId}")
            }

        } catch (e: Exception) {
            log.warn("Failed to analyze execution plan for ${project.artifactId}: ${e.message}")
        }

        return analysis
    }

    companion object {
        /**
         * Extract goal from target name (e.g., "maven-compiler-plugin:compile" -> "compile")
         */
        @JvmStatic
        fun extractGoalFromTargetName(targetName: String): String {
            val colonIndex = targetName.lastIndexOf(':')
            return if (colonIndex != -1) {
                targetName.substring(colonIndex + 1)
            } else {
                targetName
            }
        }

        /**
         * Generate target name from plugin artifact ID and goal
         */
        @JvmStatic
        fun getTargetName(pluginArtifactId: String, goal: String): String {
            return "$pluginArtifactId:$goal"
        }

        /**
         * Get common goals for well-known plugins
         */
        @JvmStatic
        fun getCommonGoalsForPlugin(artifactId: String): List<String> {
            return when (artifactId) {
                "maven-compiler-plugin" -> listOf("compile", "testCompile")
                "maven-surefire-plugin" -> listOf("test")
                "maven-failsafe-plugin" -> listOf("integration-test", "verify")
                "maven-resources-plugin" -> listOf("resources", "testResources")
                "maven-jar-plugin" -> listOf("jar")
                "maven-war-plugin" -> listOf("war")
                "maven-install-plugin" -> listOf("install")
                "maven-deploy-plugin" -> listOf("deploy")
                "maven-clean-plugin" -> listOf("clean")
                "maven-site-plugin" -> listOf("site")
                else -> emptyList()
            }
        }
    }

    /**
     * Data class to hold execution analysis for a single project
     */
    class ProjectExecutionAnalysis {
        private val phaseToGoals = LinkedHashMap<String, MutableList<String>>()
        private val goalToPhase = HashMap<String, String>()

        fun addGoalToPhase(phase: String, goal: String) {
            phaseToGoals.computeIfAbsent(phase) { arrayListOf() }.add(goal)
        }

        fun addPhaseForGoal(goal: String, phase: String) {
            goalToPhase[goal] = phase
        }

        fun getGoalsForPhase(phase: String): List<String> {
            return phaseToGoals[phase] ?: emptyList()
        }

        fun getPhaseForGoal(goal: String): String? {
            return goalToPhase[goal]
        }

        fun getTotalGoalCount(): Int = goalToPhase.size
        fun getPhaseCount(): Int = phaseToGoals.size
    }
}

