/**
 * Service for generating target groups based on Maven lifecycles
 */
class TargetGroupService(
    private val executionPlanAnalysisService: ExecutionPlanAnalysisService
) {

    fun generateTargetGroups(
        project: MavenProject,
        targets: Map<String, TargetConfiguration>,
        session: MavenSession
    ): Map<String, TargetGroup> {
        val targetGroups = linkedMapOf<String, TargetGroup>()

        // Create groups for each lifecycle
        val buildGroup = TargetGroup("build", "Build lifecycle targets", 1)
        val testGroup = TargetGroup("test", "Test lifecycle targets", 2)
        val cleanGroup = TargetGroup("clean", "Clean lifecycle targets", 3)

        // Categorize targets into groups
        for ((targetName, _) in targets) {
            when {
                targetName in listOf("compile", "package", "install", "deploy") -> {
                    buildGroup.addTarget(targetName)
                }
                targetName in listOf("test", "test-compile", "integration-test") -> {
                    testGroup.addTarget(targetName)
                }
                targetName.contains("clean") -> {
                    cleanGroup.addTarget(targetName)
                }
            }
        }

        if (buildGroup.targets.isNotEmpty()) {
            targetGroups["build"] = buildGroup
        }
        if (testGroup.targets.isNotEmpty()) {
            targetGroups["test"] = testGroup
        }
        if (cleanGroup.targets.isNotEmpty()) {
            targetGroups["clean"] = cleanGroup
        }

        return targetGroups
    }
}

