            val projectConfig = ProjectConfiguration(projectRoot).apply {
                name = projectName
                targets = projectTargets[project]?.toMutableMap() ?: mutableMapOf()
                metadata = ProjectMetadata(
                    project.groupId,
                    project.artifactId,
                    project.version,
                    project.packaging
                )
            }

            val createNodesResult = CreateNodesResult().apply {
                addProject(projectName, projectConfig)
            }

            entries.add(CreateNodesV2Entry(pomPath, createNodesResult))
        }

        return entries
    }
}

