import org.apache.maven.plugin.logging.Log
import org.apache.maven.project.MavenProject
import model.RawProjectGraphDependency
import java.io.File

/**
 * Generates project dependencies for Nx graph
 */
object CreateDependenciesGenerator {

    @JvmStatic
    fun generateCreateDependencies(
        reactorProjects: List<MavenProject>,
        workspaceRoot: File,
        log: Log,
        verbose: Boolean
    ): List<RawProjectGraphDependency> {
        val dependencies = mutableListOf<RawProjectGraphDependency>()

        // Build artifact mapping for workspace projects
        val artifactToProject = HashMap<String, MavenProject>()
        for (project in reactorProjects) {
            if (project.groupId != null && project.artifactId != null) {
                val key = MavenUtils.formatProjectKey(project)
                artifactToProject[key] = project
            }
        }

        // Generate dependencies
        for (project in reactorProjects) {
            val sourceName = NxPathUtils.getProjectName(project)
            val pomFile = NxPathUtils.getRelativePomPath(project, workspaceRoot)

            project.dependencies?.forEach { dep ->
                if (dep.groupId != null && dep.artifactId != null) {
                    val depKey = "${dep.groupId}:${dep.artifactId}"
                    val targetProject = artifactToProject[depKey]

                    if (targetProject != null && targetProject != project) {
                        val targetName = NxPathUtils.getProjectName(targetProject)
                        dependencies.add(
                            RawProjectGraphDependency(
                                sourceName,
                                targetName,
                                RawProjectGraphDependency.DependencyType.STATIC,
                                pomFile
                            )
                        )
                    }
                }
            }
        }

        return dependencies
    }
}

