import org.apache.maven.project.MavenProject
import model.*
import java.io.File

/**
 * Generates CreateNodesV2 results for Nx integration
 */
object CreateNodesResultGenerator {

    @JvmStatic
    fun generateCreateNodesV2Results(
        reactorProjects: List<MavenProject>,
        workspaceRoot: File,
        projectTargets: Map<MavenProject, Map<String, TargetConfiguration>>,
        projectTargetGroups: Map<MavenProject, Map<String, TargetGroup>>
    ): List<CreateNodesV2Entry> {
        val entries = mutableListOf<CreateNodesV2Entry>()

        for (project in reactorProjects) {
            val projectName = NxPathUtils.getProjectName(project)
            val pomPath = NxPathUtils.getRelativePomPath(project, workspaceRoot)
            val projectRoot = NxPathUtils.getRelativePath(workspaceRoot, project.basedir)

            val projectConfig = ProjectConfiguration(projectRoot).apply {
                name = projectName
                targets = projectTargets[project]?.toMutableMap() ?: mutableMapOf()
                metadata = ProjectMetadata(
                    project.groupId,
                    project.artifactId,
                    project.version,
                    project.packaging
                )
            }

            val createNodesResult = CreateNodesResult().apply {
                addProject(projectName, projectConfig)
            }

            entries.add(CreateNodesV2Entry(pomPath, createNodesResult))
        }

        return entries
    }
}
