 25    @Override
 26    public InvocationResult execute(InvocationRequest request) throws MavenInvocationException {
 27        MavenCommandLineBuilder cliBuilder = new MavenCommandLineBuilder();
 28
 29        InvokerLogger logger = getLogger();
 30        if (logger != null) {
 31            cliBuilder.setLogger(getLogger());
 32        }
 33
 34        File localRepo = getLocalRepositoryDirectory();
 35        if (localRepo != null) {
 36            cliBuilder.setLocalRepositoryDirectory(getLocalRepositoryDirectory());
 37        }
 38
 39        File mavenHome = getMavenHome();
 40        if (mavenHome != null) {
 41            cliBuilder.setMavenHome(getMavenHome());
 42        }
 43
 44        File mavenExecutable = getMavenExecutable();
 45        if (mavenExecutable != null) {
 46            cliBuilder.setMavenExecutable(mavenExecutable);
 47        }
 48
 49        File workingDirectory = getWorkingDirectory();
 50        if (workingDirectory != null) {
 51            cliBuilder.setBaseDirectory(getWorkingDirectory());
 52        }
 53
 54        request.setBatchMode(true);
 55
 56        Commandline cli;
 57        try {
 58            cli = cliBuilder.build(request);
 59            if (logger != null) {
 60                logger.debug("Running Maven CLI command: " + cli);
 61            }
 62        } catch (CommandLineConfigurationException e) {
 63            throw new MavenInvocationException("Error configuring command-line. Reason: " + e.getMessage(), e);
 64        }
 65
 66        MavenProcessInvocationResult result = new MavenProcessInvocationResult();
 67
 68        try {
 69            Process process = executeCommandLine(cli, request);
 70            result.setProcess(process);
 71        } catch (CommandLineException e) {
 72            result.setException(e);
 73        }
 74
 75        return result;
 76    }
 77
 78    private Process executeCommandLine(Commandline cli, InvocationRequest request)
 79            throws CommandLineException {
 80        InvocationOutputHandler outputHandler = request.getOutputHandler(this.outputHandler);
 81        InvocationOutputHandler errorHandler = request.getErrorHandler(this.errorHandler);
 82        return executeCommandLine(cli, outputHandler, errorHandler);
 83    }
 84
 85    private static Process executeCommandLine(Commandline cl, StreamConsumer systemOut, StreamConsumer systemErr)
 86            throws CommandLineException {
 87        if (cl == null) {
 88            throw new IllegalArgumentException("the command line cannot be null.");
 89        } else {
 90            final Process p = cl.execute();
 91            final StreamPumper outputPumper = new StreamPumper(p.getInputStream(), systemOut);
 92            final StreamPumper errorPumper = new StreamPumper(p.getErrorStream(), systemErr);
 93
 94            outputPumper.start();
 95            errorPumper.start();
 96
 97            new Thread(() -> {
 98                try {
 99                    // Wait for termination
100                    p.waitFor();
101                    outputPumper.waitUntilDone();
102                    errorPumper.waitUntilDone();
103                } catch (Exception e) {
104                    outputPumper.disable();
105                    errorPumper.disable();
106                    e.printStackTrace();
107                }
108            }).start();
109
110            return p;
111        }
112    }
