199    @Override
200    public BuildCommandArgs prepareAction(String action, BuildOptions buildOptions, RunModeOption runMode,
201            List<String> params) {
202        ArrayDeque<String> args = new ArrayDeque<>();
203        setMavenProperties(args, runMode.isBatchMode());
204
205        if (runMode.isBatchMode()) {
206            args.add("-B");
207        }
208
209        if (buildOptions.offline) {
210            args.add("--offline");
211        }
212
213        if (buildOptions.clean) {
214            args.add("clean");
215        }
216        args.add(action);
217
218        if (buildOptions.buildNative) {
219            args.add("-Dnative");
220        }
221        if (buildOptions.skipTests()) {
222            setSkipTests(args);
223        }
224
225        // Add any other unmatched arguments
226        args.addAll(params);
227
228        return prependExecutable(args);
229    }
230
231    @Override
232    public BuildCommandArgs prepareTest(BuildOptions buildOptions, RunModeOption runMode, List<String> params, String filter) {
233        if (filter != null) {
234            params.add("-Dtest=" + filter);
235        }
236        return prepareAction("test", buildOptions, runMode, params);
237    }
238
239    @Override
240    public List<Supplier<BuildCommandArgs>> prepareDevTestMode(boolean devMode, DevOptions commonOptions,
241            DebugOptions debugOptions, List<String> params) {
242        ArrayDeque<String> args = new ArrayDeque<>();
243        List<String> jvmArgs = new ArrayList<>();
244
245        setMavenProperties(args, false);
246
247        if (commonOptions.clean) {
248            args.add("clean");
249        }
250        args.add(devMode ? "quarkus:dev" : "quarkus:test");
251
252        if (commonOptions.offline) {
253            args.add("--offline");
254        }
255
256        debugOptions.addDebugArguments(args, jvmArgs);
257        propertiesOptions.flattenJvmArgs(jvmArgs, args);
258
259        // Add any other unmatched arguments
260        paramsToQuarkusArgs(params, args);
261
262        BuildCommandArgs buildCommandArgs = prependExecutable(args);
263        return Collections.singletonList(() -> buildCommandArgs);
264    }
265
266    void setSkipTests(ArrayDeque<String> args) {
267        args.add("-DskipTests");
268        args.add("-Dmaven.test.skip=true");
269    }
270
271    void setMavenProperties(ArrayDeque<String> args, boolean batchMode) {
272        if (output.isShowErrors()) {
273            args.addFirst("-e");
274        }
275        // batch mode typically disables ansi/color output
276        if (output.isAnsiEnabled() && !batchMode) {
277            args.addFirst("-Dstyle.color=always");
278        }
279
280        String mavenSettings = propertiesOptions.properties.remove(MAVEN_SETTINGS);
281        if (mavenSettings != null && !mavenSettings.isEmpty()) {
282            args.add("-s");
283            args.add(mavenSettings);
284        } else {
285            mavenSettings = System.getProperty(MAVEN_SETTINGS);
286            if (mavenSettings != null && !mavenSettings.isEmpty() && Files.exists(Path.of(mavenSettings))) {
287                args.add("-s");
288                args.add(mavenSettings);
289            }
290        }
291
292        // add specified properties
293        args.addAll(flattenMappedProperties(propertiesOptions.properties));
294        args.add(registryClient.getRegistryClientProperty());
295
296        final String configFile = registryClient.getConfigArg() == null
297                ? System.getProperty(RegistriesConfigLocator.CONFIG_FILE_PATH_PROPERTY)
298                : registryClient.getConfigArg();
299        if (configFile != null) {
300            args.add("-D" + RegistriesConfigLocator.CONFIG_FILE_PATH_PROPERTY + "=" + configFile);
301        }
302    }
303
304    void verifyBuildFile() {
305        File buildFile = projectRoot.resolve("pom.xml").toFile();
306        if (!buildFile.isFile()) {
307            throw new IllegalStateException("Is this a project directory? Unable to find a build file in: " + projectRoot);
308        }
309    }
310}
