package com.example.maven;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Arrays;
import java.util.Collections;
import java.util.List;
import java.util.Properties;
import java.util.stream.Stream;

import org.apache.maven.cli.MavenCli;
import org.apache.maven.shared.invoker.DefaultInvocationRequest;
import org.apache.maven.shared.invoker.DefaultInvoker;
import org.apache.maven.shared.invoker.InvocationRequest;
import org.apache.maven.shared.invoker.InvocationResult;
import org.apache.maven.shared.invoker.Invoker;
import org.apache.maven.shared.invoker.MavenInvocationException;
import org.codehaus.plexus.classworlds.ClassWorld;

/**
 * Multi-project Maven executor using Maven Embedder API
 * Executes various Maven goals across multiple projects and phases
 */
public class MultiProjectMavenExecutor {

    private final ClassWorld classWorld;
    private final MavenCli cli;

    public MultiProjectMavenExecutor() {
        this.classWorld = new ClassWorld("plexus.core", Thread.currentThread().getContextClassLoader());
        this.cli = new MavenCli(classWorld);
    }

    /**
     * Execute multiple goals across multiple projects using Maven Embedder
     */
    public void executeMultiProject(String rootDir, List<String> projectPaths, List<String> goals) {
        System.out.println("Starting multi-project Maven execution...");
        System.out.println("Root directory: " + rootDir);
        System.out.println("Projects: " + projectPaths);
        System.out.println("Goals: " + goals);

        for (String projectPath : projectPaths) {
            Path fullPath = Paths.get(rootDir, projectPath);
            if (!Files.exists(fullPath.resolve("pom.xml"))) {
                System.err.println("Skipping " + projectPath + " - no pom.xml found");
                continue;
            }

            System.out.println("\n=== Executing goals for project: " + projectPath + " ===");
            executeGoalsForProject(fullPath.toString(), goals);
        }
    }

    /**
     * Execute goals for a single project using Maven Embedder
     */
    private void executeGoalsForProject(String projectDir, List<String> goals) {
        try {
            // Prepare system properties
            Properties originalProps = System.getProperties();
            Properties executionProps = new Properties(originalProps);

            // Set Maven-specific properties
            executionProps.setProperty("maven.multiModuleProjectDirectory", projectDir);
            executionProps.setProperty("user.dir", projectDir);

            System.setProperties(executionProps);

            // Build command line arguments
            String[] args = buildMavenArgs(goals);

            System.out.println("Executing: mvn " + String.join(" ", args));
            System.out.println("Working directory: " + projectDir);

            // Execute using Maven CLI (embedder approach)
            int result = cli.doMain(args, projectDir, System.out, System.err);

            if (result == 0) {
                System.out.println(" Successfully executed goals: " + goals);
            } else {
                System.err.println(" Failed to execute goals: " + goals + " (exit code: " + result + ")");
            }

            // Restore original properties
            System.setProperties(originalProps);

        } catch (Exception e) {
            System.err.println("Error executing goals for project " + projectDir + ": " + e.getMessage());
            e.printStackTrace();
        }
    }

    /**
     * Execute lifecycle phases across multiple projects
     */
    public void executeLifecyclePhases(String rootDir, List<String> projectPaths, String... phases) {
        System.out.println("Starting lifecycle phase execution...");
        System.out.println("Phases: " + Arrays.toString(phases));

        for (String phase : phases) {
            System.out.println("\n=== PHASE: " + phase + " ===");
            for (String projectPath : projectPaths) {
                Path fullPath = Paths.get(rootDir, projectPath);
                if (!Files.exists(fullPath.resolve("pom.xml"))) {
                    System.err.println("Skipping " + projectPath + " - no pom.xml found");
                    continue;
                }

                System.out.println("Executing " + phase + " for " + projectPath);
                executeGoalsForProject(fullPath.toString(), Collections.singletonList(phase));
            }
        }
    }

    /**
     * Alternative implementation using Maven Invoker API for comparison
     */
    public void executeWithInvokerAPI(String projectDir, List<String> goals) {
        System.out.println("\n=== Using Maven Invoker API (comparison) ===");

        try {
            InvocationRequest request = new DefaultInvocationRequest();
            request.setPomFile(new File(projectDir, "pom.xml"));
            request.setGoals(goals);
            request.setBaseDirectory(new File(projectDir));

            Invoker invoker = new DefaultInvoker();

            System.out.println("Invoker API executing: " + goals + " in " + projectDir);
            InvocationResult result = invoker.execute(request);

            if (result.getExitCode() == 0) {
                System.out.println(" Invoker API succeeded");
            } else {
                System.err.println(" Invoker API failed (exit code: " + result.getExitCode() + ")");
                if (result.getExecutionException() != null) {
                    result.getExecutionException().printStackTrace();
                }
            }
        } catch (MavenInvocationException e) {
            System.err.println("Maven Invoker error: " + e.getMessage());
            e.printStackTrace();
        }
    }

    /**
     * Build Maven command line arguments
     */
    private String[] buildMavenArgs(List<String> goals) {
        return goals.toArray(new String[0]);
    }

    /**
     * Discover Maven projects in a directory tree
     */
    public List<String> discoverProjects(String rootDir) {
        try (Stream<Path> paths = Files.walk(Paths.get(rootDir))) {
            return paths
                .filter(path -> path.getFileName().toString().equals("pom.xml"))
                .map(path -> Paths.get(rootDir).relativize(path.getParent()).toString())
                .filter(path -> !path.isEmpty()) // Skip root project
                .sorted()
                .toList();
        } catch (IOException e) {
            System.err.println("Error discovering projects: " + e.getMessage());
            return Collections.emptyList();
        }
    }

    /**
     * Main method demonstrating usage
     */
    public static void main(String[] args) {
        MultiProjectMavenExecutor executor = new MultiProjectMavenExecutor();

        // Example configuration
        String rootDirectory = System.getProperty("user.dir");

        System.out.println("Maven Multi-Project Executor");
        System.out.println("============================");

        // Discover projects automatically
        List<String> projects = executor.discoverProjects(rootDirectory);
        System.out.println("Discovered projects: " + projects);

        if (projects.isEmpty()) {
            System.out.println("No sub-projects found. Executing on root project.");
            projects = Collections.singletonList(".");
        }

        // Example 1: Execute specific goals across all projects
        System.out.println("\n--- Example 1: Compile all projects ---");
        executor.executeMultiProject(rootDirectory, projects.subList(0, Math.min(3, projects.size())), 
                                    Arrays.asList("clean", "compile"));

        // Example 2: Execute lifecycle phases
        System.out.println("\n--- Example 2: Lifecycle phases ---");
        executor.executeLifecyclePhases(rootDirectory, projects.subList(0, Math.min(2, projects.size())), 
                                      "validate", "compile", "test-compile");

        // Example 3: Comparison with Invoker API
        if (!projects.isEmpty()) {
            String firstProject = Paths.get(rootDirectory, projects.get(0)).toString();
            System.out.println("\n--- Example 3: Invoker API comparison ---");
            executor.executeWithInvokerAPI(firstProject, Arrays.asList("clean", "compile"));
        }

        System.out.println("\n=== Execution completed ===");
    }
}

